mapper.select:
select
experiment_comment.id, content, user_id, experiment_comment.create_time, experiment_id, username
from experiment_comment
left join user u on u.id = experiment_comment.user_id


        and experiment_comment.id = #{id}


        and content = #{content}


        and user_id = #{userId}


        and username like concat('%', #{username}, '%')


        and experiment_comment.create_time = #{createTime}


        and experiment_id = #{experimentId}

select
    experiment_comment.id, reply_id, content, username, user_id, experiment_comment.create_time, experiment_id
from experiment_comment
         left join user u on u.id = experiment_comment.user_id

     and reply_id is null

        and experiment_comment.id = #{id}


        and content = #{content}


        and user_id = #{userId}


        and reply_id = #{replyId}


        and experiment_comment.create_time = #{createTime}


        and experiment_id = #{experimentId}
